// timeline.service.ts
import { collection, query, where, orderBy, limit, getDocs, onSnapshot } from 'firebase/firestore';
import { db } from '../firebase';

export class TimelineService {
  async getTimelineEvents(userId: string, page: number = 1, pageSize: number = 20): Promise<any[]> {
    try {
      const timelineRef = collection(db, 'timeline');
      const q = query(
        timelineRef,
        where('participantIds', 'array-contains', userId),
        orderBy('timestamp', 'desc'),
        limit(page * pageSize)
      );

      const snapshot = await getDocs(q);
      return snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
    } catch (error) {
      console.error('Error fetching timeline events:', error);
      throw error;
    }
  }

  subscribeToTimelineUpdates(userId: string, callback: (events: any[]) => void): () => void {
    const timelineRef = collection(db, 'timeline');
    const q = query(
      timelineRef,
      where('participantIds', 'array-contains', userId),
      orderBy('timestamp', 'desc'),
      limit(50)
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const events = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      callback(events);
    }, (error) => {
      console.error('Timeline subscription error:', error);
    });

    return unsubscribe;
  }

  async addTimelineEvent(event: {
    type: 'connection' | 'message' | 'nft' | 'media' | 'custom';
    participantIds: string[];
    content: any;
    timestamp?: number;
  }): Promise<string> {
    try {
      const timelineRef = collection(db, 'timeline');
      const eventData = {
        ...event,
        timestamp: event.timestamp || Date.now()
      };

      const docRef = await addDoc(timelineRef, eventData);
      return docRef.id;
    } catch (error) {
      console.error('Error adding timeline event:', error);
      throw error;
    }
  }
}
```

