```typescript
// chat.service.ts
import { collection, query, where, orderBy, limit, addDoc, onSnapshot, updateDoc, doc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { db, storage } from '../firebase';

export class ChatService {
  async sendMessage(chatId: string, senderId: string, content: string, type: 'text' | 'image' = 'text'): Promise<string> {
    try {
      const messagesRef = collection(db, 'chats', chatId, 'messages');
      const messageData = {
        senderId,
        content,
        type,
        timestamp: Date.now(),
        status: 'sent',
        reactions: {}
      };

      const docRef = await addDoc(messagesRef, messageData);

      // Update last message in chat metadata
      const chatRef = doc(db, 'chats', chatId);
      await updateDoc(chatRef, {
        lastMessage: content,
        lastMessageTimestamp: messageData.timestamp,
        lastMessageSenderId: senderId
      });

      return docRef.id;
    } catch (error) {
      console.error('Error sending message:', error);
      throw error;
    }
  }

  async uploadImage(chatId: string, senderId: string, uri: string): Promise<string> {
    try {
      const response = await fetch(uri);
      const blob = await response.blob();

      const storageRef = ref(storage, `chats/${chatId}/${Date.now()}_${senderId}`);
      await uploadBytes(storageRef, blob);

      const downloadURL = await getDownloadURL(storageRef);
      await this.sendMessage(chatId, senderId, downloadURL, 'image');

      return downloadURL;
    } catch (error) {
      console.error('Error uploading image:', error);
      throw error;
    }
  }

  subscribeToMessages(chatId: string, callback: (messages: any[]) => void): () => void {
    const messagesRef = collection(db, 'chats', chatId, 'messages');
    const q = query(
      messagesRef,
      orderBy('timestamp', 'desc'),
      limit(50)
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const messages = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      callback(messages.reverse());
    }, (error) => {
      console.error('Chat subscription error:', error);
    });

    return unsubscribe;
  }

  async addReaction(chatId: string, messageId: string, userId: string, reaction: string): Promise<void> {
    try {
      const messageRef = doc(db, 'chats', chatId, 'messages', messageId);
      await updateDoc(messageRef, {
        [`reactions.${userId}`]: reaction
      });
    } catch (error) {
      console.error('Error adding reaction:', error);
      throw error;
    }
  }

  getConversationStarters(compatibilityData: any): string[] {
    // Generate conversation starters based on astrological compatibility
    // This could be expanded with more sophisticated logic
    const starters = [
      `As a ${compatibilityData.signs.user1Sun}, what do you think about...`,
      `Your ${compatibilityData.elements.user1Moon} moon might enjoy...`,
      `With your Venus in ${compatibilityData.planets.user1Venus}, how do you express affection?`
    ];

    return starters;
  }
}
