// CompatibilityScreen.tsx
import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, ActivityIndicator } from 'react-native';
import { useRoute } from '@react-navigation/native';
import { AstrologyService } from '../services/astrology.service';
import { NftService } from '../services/nft.service';
import { CompatibilityChart } from '../components/CompatibilityChart';
import { IceBreaker } from '../components/IceBreaker';
import { ScoreCircle } from '../components/ScoreCircle';
import { useAuth } from '../hooks/useAuth';

export const CompatibilityScreen = () => {
  const route = useRoute();
  const { user1Data, user2Data } = route.params;
  const { currentUser } = useAuth();
  const [loading, setLoading] = useState(true);
  const [compatibility, setCompatibility] = useState(null);
  const [minting, setMinting] = useState(false);
  const [mintedNft, setMintedNft] = useState(null);

  const astrologyService = new AstrologyService();
  const nftService = new NftService();

  useEffect(() => {
    const calculateCompatibility = async () => {
      try {
        const result = await astrologyService.calculateCompatibility(user1Data, user2Data);
        setCompatibility(result);
      } catch (error) {
        console.error('Error calculating compatibility:', error);
        // Handle error
      } finally {
        setLoading(false);
      }
    };

    calculateCompatibility();
  }, [user1Data, user2Data]);

  const handleMintNft = async () => {
    try {
      setMinting(true);
      const location = await getCurrentLocation();

      const txHash = await nftService.mintConnectionNFT(
        currentUser.address,
        user2Data.address,
        compatibility,
        location
      );

      setMintedNft({
        txHash,
        timestamp: Date.now()
      });
    } catch (error) {
      console.error('Error minting NFT:', error);
      // Handle error
    } finally {
      setMinting(false);
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#8A2BE2" />
        <Text style={styles.loadingText}>Calculating cosmic compatibility...</Text>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Cosmic Connection</Text>
        <ScoreCircle score={compatibility.compatibilityScore} />
      </View>

      <CompatibilityChart aspects={compatibility.aspects} />

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Conversation Starters</Text>
        {compatibility.iceBreakers.map((iceBreaker, index) => (
          <IceBreaker key={index} text={iceBreaker} />
        ))}
      </View>

      <TouchableOpacity 
        style={[styles.mintButton, minting && styles.mintingButton]}
        onPress={handleMintNft}
        disabled={minting || mintedNft}
      >
        {minting ? (
          <ActivityIndicator color="#fff" />
        ) : mintedNft ? (
          <Text style={styles.buttonText}>NFT Minted! âœ¨</Text>
        ) : (
          <Text style={styles.buttonText}>Mint This Connection</Text>
        )}
      </TouchableOpacity>

      {mintedNft && (
        <View style={styles.nftInfo}>
          <Text style={styles.nftText}>Transaction: {mintedNft.txHash}</Text>
          <TouchableOpacity>
            <Text style={styles.viewNftText}>View in Wallet</Text>
          </TouchableOpacity>
        </View>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
