// notification.service.ts
import { Platform } from 'react-native';
import * as Device from 'expo-device';
import * as Notifications from 'expo-notifications';
import { getMessaging, getToken, onMessage } from 'firebase/messaging';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import { db } from '../firebase';
import { AstrologyService } from './astrology.service';

export class NotificationService {
  private astrologyService: AstrologyService;

  constructor() {
    this.astrologyService = new AstrologyService();

    // Configure notification behavior
    Notifications.setNotificationHandler({
      handleNotification: async () => ({
        shouldShowAlert: true,
        shouldPlaySound: true,
        shouldSetBadge: true,
      }),
    });

    // Listen for foreground notifications
    onMessage(getMessaging(), (message) => {
      Notifications.scheduleNotificationAsync({
        content: {
          title: message.notification.title,
          body: message.notification.body,
          data: message.data,
        },
        trigger: null,
      });
    });
  }

  async registerForPushNotifications(userId: string): Promise<string> {
    if (!Device.isDevice) {
      throw new Error('Push notifications are not supported in the simulator');
    }

    const { status: existingStatus } = await Notifications.getPermissionsAsync();
    let finalStatus = existingStatus;

    if (existingStatus !== 'granted') {
      const { status } = await Notifications.requestPermissionsAsync();
      finalStatus = status;
    }

    if (finalStatus !== 'granted') {
      throw new Error('Permission not granted for push notifications');
    }

    let token;

    if (Platform.OS === 'android') {
      // For Android, we use Firebase Cloud Messaging
      token = await getToken(getMessaging(), {
        vapidKey: process.env.EXPO_PUBLIC_FCM_VAPID_KEY,
      });
    } else {
      // For iOS, we use APNs via Expo
      const { data } = await Notifications.getExpoPushTokenAsync();
      token = data;
    }

    // Store the token in Firestore
    await setDoc(doc(db, 'users', userId), {
      pushToken: token,
      platform: Platform.OS,
      updatedAt: Date.now(),
    }, { merge: true });

    return token;
  }

  async scheduleHoroscopeNotification(userId: string): Promise<void> {
    try {
      // Get user's birth data
      const userDoc = await getDoc(doc(db, 'users', userId));
      const userData = userDoc.data();

      if (!userData.birthData) {
        return;
      }

      // Get today's horoscope
      const horoscope = await this.astrologyService.getDailyHoroscope(userData.birthData);

      // Schedule notification for preferred time (default 9 AM)
      const preferredTime = userData.notificationPreferences?.horoscopeTime || '09:00';
      const [hours, minutes] = preferredTime.split(':').map(Number);

      const trigger = new Date();
      trigger.setHours(hours, minutes, 0, 0);

      // If time has passed for today, schedule for tomorrow
      if (trigger < new Date()) {
        trigger.setDate(trigger.getDate() + 1);
      }

      await Notifications.scheduleNotificationAsync({
        content: {
          title: `Daily Horoscope for ${userData.birthData.sunSign}`,
          body: horoscope.prediction,
          data: { type: 'horoscope', userId },
        },
        trigger,
      });
    } catch (error) {
      console.error('Error scheduling horoscope notification:', error);
    }
  }

  async sendChatNotification(recipientId: string, senderId: string, message: string): Promise<void> {
