// analytics.service.ts
import { getAnalytics, logEvent, setUserProperties } from 'firebase/analytics';
import { getPerformance, trace } from 'firebase/performance';
import { app } from '../firebase';

export class AnalyticsService {
  private analytics;
  private performance;

  constructor() {
    this.analytics = getAnalytics(app);
    this.performance = getPerformance(app);
  }

  setUserProfile(userId: string, properties: Record<string, string>): void {
    setUserProperties(this.analytics, properties);
  }

  trackScreenView(screenName: string): void {
    logEvent(this.analytics, 'screen_view', {
      firebase_screen: screenName,
    });
  }

  trackConnection(connectionType: 'qr' | 'nfc', successfulPairing: boolean, timeToComplete: number): void {
    logEvent(this.analytics, 'connection_attempt', {
      connection_type: connectionType,
      successful: successfulPairing,
      time_to_complete: timeToComplete,
    });
  }

  trackNftMint(successful: boolean, timeToComplete: number): void {
    logEvent(this.analytics, 'nft_mint', {
      successful,
      time_to_complete: timeToComplete,
    });
  }

  trackSubscription(tier: string, price: number, successful: boolean): void {
    logEvent(this.analytics, 'subscription_purchase', {
      tier,
      price,
      successful,
    });
  }

  trackAppClipConversion(installed: boolean): void {
    logEvent(this.analytics, 'app_clip_conversion', {
      installed,
    });
  }

  startTrace(traceName: string): any {
    return trace(this.performance, traceName);
  }

  // Custom conversion funnel tracking
  trackConnectionFunnel(stage: 'initiated' | 'paired' | 'compatibility_viewed' | 'nft_minted'): void {
    logEvent(this.analytics, 'connection_funnel', {
      stage,
      timestamp: Date.now(),
    });
  }

  // A/B testing variant tracking
  trackExperimentVariant(experimentId: string, variant: string): void {
    logEvent(this.analytics, 'experiment_impression', {
      experiment_id: experimentId,
      variant,
    });
  }
}
